import React, { useEffect, useState } from 'react'
import "./App.css"
import Job from "./Container/Job/Job"
import Tablets from './Container/Tablets/Tablets'
import DataItem from "./data.json"
function App() {


  const [require,setRequire]=useState([])
  const [display,setDisplay]=useState(false)

  const [jsonData, setJsonData] = useState(null);

  const [allItem,setAllItem]=useState(DataItem['items'])


  useEffect(()=>{

    if(require.length){
      setDisplay(true)

      const items=allItem.filter((obj)=>{
            obj.languages.som((item)=>
              require.includes(item)
            )
      })


    }
    if(require.length==0){
      setAllItem(DataItem['items'])
    }

  },[require])




  return (
    <div className="Static-container">

        <div className="header-container"></div>
        <div className="main-container">

                <div className="main-content">
                {display && <Tablets require={require} setRequire={setRequire} setDisplay={setDisplay} />}
                {
                  allItem.map((item, index) => (
                    <Job  key={index} require={require} setRequire={setRequire} 
                    item={item}  /> 
                    )
                  )
                }
                </div>
        </div>
    </div>
  )
}

export default App
